async function $toggle(key, o) {
    if (o == null) {
        this[key] = !this[key]
        return 
    }
    o.key = key
    const value = await asyncToggle(this, o)
    if (o.force) {
        this.$forceUpdate()
    }
    return value
}

async function $animate(el, key, ...args) {
    // console.log({key, args, el})
    try {
        return await animations[key](el, ...args)
    } catch(e) {
        console.log("ERRORRRRRRRR", e.stack)
        console.log(el, key, args)
    }
}

function $log(key, transform) {
    chalk.blue(key + ':', true)
    const value = this.$get(key, transform)
    const computed = isDefined(value) ? value : 'NULL'
    console.log(computed)
}

function $get(key, transform) {
    const get = (key) => {
        const _value = key in this.$options ? this.$options[key] : '$' + key in this ? this['$' + key] : this[key]
        return _value
    }
    const _value = get(key)
    const value = transform ? transform(_value) : _value
    return value
}

function $modularIndex(key, dir = 1) {
    const indexKey = this.$options.indexKey || 'index'
    const currentIndex = this[indexKey]
    const items = this[key]
    const nextIndex = modularIncrementIndex(items.length, currentIndex, dir)
    if (isNaN(nextIndex)) {
        console.log('error ... it is nan')
    }
    this[indexKey] = nextIndex
}

function $modular(key, value) {
    return boundModularIncrement(this, key, value)
}



