function _get(state, key) {
    switch(key) {
        case 'template':
            return state.$options.template
        case 'raw':
            return state.$options.raw
        case 'methods':
            return Object.keys(state.$options.methods || [])
        case 'computed':
            return Object.keys(state.$options.computed || [])
        case 'name':
            return state.$options.name
        case 'props':
            return state.$options.props
        case 'data':
            return state.$data
        case 'uid':
            return state._uid
    }
}

function $info(...keys) {
    return dict(keys, (key) => _get(this, key))
}


