class VueProxy {
    constructor(keys) {
        this.keys = keys
    }
    // Define it on the 
    init(vue) {
        // these are the only internals allowed
        // setting is not allowed
        // accessing the vue instance is not allowed
        for (const key of this.keys) {
            const get = () => vue[key]
            Object.defineProperty(this, key, {get})
        }
        const refKeys = Object.keys(vue.$refs || {})
        for (const key of refKeys) {
            Object.defineProperty(this, key, () => this.$refs[key])
        }
        // const methods = Object.keys(vue.$options?.methods || {})

        // for (const key of methods) {
            // Object.defineProperty(this, key, () => this.$refs[key]
        // }
    }
}
