class Keymap {
    constructor(options) {
        this.store = {}
    }
    add(binding, func) {
        this.store[binding] = func
    }
    call(vue, e) {
        function fix(s) {
            return s.replace(/(?:Control|yypdfg)/, 'ctrl-')
            return s.replace(/(?:ctrl|yypdfg)/, 'Control')
        }
        try {
            const m = getKeyAndType(e)
            if (!m) {
                return 
            }
            const [keyName, primaryType, secondaryType] = m
            const func = this.store[fix(keyName)]
            if (!func) {
                return 
            }
            func(vue)
            e.preventDefault()
            return true
        } catch(e) {
            console.log("ERRORRRRRRRR", e.stack)
            return 
        }
    }
}
const $keymap = new Keymap()
