function $listen(opts) {
    if (!this.$keyListener) {
        this.$keyListener = new KeyListener(this, opts)
    }
}
class KeyListener {
    constructor(vue, handlers) {
        function listener(e) {
            const inputState = getInputState(e)
            if (!inputState) {
                return 
            }
            const { arg, name, type } = inputState
            const fn = handlers[arg] || handlers[name] || handlers[type + "Key"]
            if (fn) {
                fn.call(vue, arg)
            }
        }
        window.addEventListener('keydown', listener)
    }
}
